template <typename Dlg, typename D> int PPDialogProcBodyID(uint dlgID, D * pData)
{
	int    ok = -1;
	Dlg * dlg = new Dlg(dlgID);
	if(CheckDialogPtrErr(&dlg) && dlg->setDTS(pData)) {
		while(ok <= 0 && ExecView(dlg) == cmOK)
			if(dlg->getDTS(pData))
				ok = 1;
	}
	else
		ok = 0;
	delete dlg;
	return ok;
}

